// this line should not be removed
var wCustomers = angular.module("wCustomers",[]);

wCustomers.controller( "wCustomersController", function( $scope, Customer ) {

	var dq = new DataQuery( 'qCustomers' );
    dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.customers = dq.getData().data;
		});
	});


});

