var wDhatim = angular.module("wDhatim",[]);

wDhatim.controller( "wDhatimController", function( $scope ) {
	var dq = new DataQuery( 'qTwitter' );
    dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
			console.log($scope.results);
		});
	});

});
